import React from "react";

const Index = () => (
  <div className="dark-mode">
    <div className="hero-container" id="about">
      <h1>Hi üëãüèº, I'm Nhat & I'm a Front-End Developer</h1>
      <p>
        I'm passionate about designing and building intuitive and beautiful
        software applications.
        <br />
        When I'm not working on my craft, I'm probably hanging out with my
        family.
      </p>
      <div className="work-btn">
        <a href="">MY WORK</a>
      </div>
    </div>

    <div className="waves-container">
      <svg
        width="100%"
        height="100%"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path fill="white">
          <animate
            repeatCount="indefinite"
            fill="freeze"
            attributeName="d"
            dur="4s"
            values="M0 25.9086C277 84.5821 433 65.736 720 25.9086C934.818 -3.9019 1214.06 -5.23669 1442 8.06597C2079 45.2421 2208 63.5007 2560 25.9088V171.91L0 171.91V25.9086Z;
            M0 86.3149C316 86.315 444 159.155 884 51.1554C1324 -56.8446 1320.29 34.1214 1538 70.4063C1814 116.407 2156 188.408 2560 86.315V232.317L0 232.316V86.3149Z;
            M0 53.6584C158 11.0001 213 0 363 0C513 0 855.555 115.001 1154 115.001C1440 115.001 1626 -38.0004 2560 53.6585V199.66L0 199.66V53.6584Z;
            M0 25.9086C277 84.5821 433 65.736 720 25.9086C934.818 -3.9019 1214.06 -5.23669 1442 8.06597C2079 45.2421 2208 63.5007 2560 25.9088V171.91L0 171.91V25.9086Z
            "
          />
        </path>
      </svg>
    </div>
    <style jsx>{`
      body {
        font-family: avenir, proxima nova, helvetica neue, Helvetica, Arial,
          sans-serif;
        font-weight: 400;
        margin: 0;
        font-size: 20px;
        -webkit-font-smoothing: antialiased;
        background-color: white;
      }

      .bg-is-dark {
        background-color: #62cea1;
      }

      .format-disabled {
        text-decoration: none;
        list-style: none;
        color: white;
      }

      .hide {
        display: none;
      }
      .show {
        display: block;
      }

      .hide-w-opacity {
        opacity: 0;
      }

      .show-w-opacity {
        opacity: 1;
      }

      .animated-transition {
        transition: 1s cubic-bezier(0.2, 0.8, 0.2, 1);
      }

      .m-container {
        padding: 0px 40px;
        min-width: 1200px;
      }

      .sm-container {
        padding: 0px 100px;
      }

      .is-center {
        display: flex;
        justify-content: center;
        align-items: center;
      }

      .demo-btn {
        cursor: pointer;
        color: #14f1d9;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        font-size: 20px;
        border-radius: 12px;
        border: 1px solid black;
        transition: 1s cubic-bezier(0.2, 0.8, 0.2, 1);
        padding: 10px;
        font-weight: 700;
        border: none;
      }

      .demo-btn:hover {
        box-shadow: 0 10px 20px rgba(0, 0, 0, 0.25);
        transform: translateY(-10px);
        color: white;
        background-color: #14f1d9;
      }

      @media (max-width: 640px) {
        .hero-container h1 {
          font-size: 34px;
        }

        .hero-container p {
          font-size: 26px;
        }
      }

      @keyframes hero-animation {
        0% {
          opacity: 0;
          transform: translateY(30px);
        }
        100% {
          opacity: 1;
          transform: translateY(0px);
        }
      }
    `}</style>
  </div>
);
export default Index;
