import React from "react";

//components
import Hero from "./components/Hero";

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: "dark"
    };
  }

  render() {
    return (
      <div>
        <style global jsx>{`
          body {
            font-family: avenir, proxima nova, helvetica neue, Helvetica, Arial,
              sans-serif;
            font-weight: 400;
            margin: 0;
            padding: 0;
            font-size: 20px;
            -webkit-font-smoothing: antialiased;
          }

          .dark-mode {
            background-color: #62cea1;
          }

          .light-mode {
            background-color: white;
          }

          .format-disabled {
            text-decoration: none;
            list-style: none;
            color: white;
          }

          .hide {
            display: none;
          }
          .show {
            display: block;
          }

          .hide-w-opacity {
            opacity: 0;
          }

          .show-w-opacity {
            opacity: 1;
          }

          .animated-transition {
            transition: 1s cubic-bezier(0.2, 0.8, 0.2, 1);
          }

          .m-container {
            padding: 0px 40px;
            min-width: 1200px;
          }

          .sm-container {
            padding: 0px 100px;
          }

          .is-center {
            display: flex;
            justify-content: center;
            align-items: center;
          }
        `}</style>
      </div>
    );
  }
}
export default Index;
